import "../styles/Buttons.css";
import "../styles/Home.css";


function Home() {
  React.useEffect (() => {
    Aos.init({ duration: 1000 });
  }, []);

  return (
    <div>
      <div className="container">
        <div className="home-grid">
          <div data-aos='zoom-in'>
          <div>
            <p className='subtitle'>Hi. my name is Nicol√°s Rouger, but call me</p>
            <h1 data-aos="fade-right" className="title">
              Nico
            </h1>
            <p className="" style={{margin: "1rem"}}>I'm a FullStack Web Developer.
            Passionate about the development of a user experience from scratch.</p>
            <div className='line-left'></div>

          </div>
          </div>
        </div>
      </div>
      <div>
        <div>
          <Link to="/portfolio" className="material-bubble"> {/*data-aos="fade" data-aos-offset="100"*/}
            <p className="" style={{ margin: "3rem 1rem"}}  >Get to know me</p>
            {/* <button class="material-bubble">Xenization</button> */}
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Home;
