import React from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import Home from './Home';
import Portfolio from './Portfolio';
import '../styles/Transitions.css'; // Import the CSS file for transitions


function App() {
    // Custom hook to manage scroll position on page transitions
  const location = useLocation();
  React.useEffect(() => {
    window.scrollTo(0, 0);
  }, [location,])

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route
        path="/portfolio"
        element={<Portfolio />}
        enterClassName="fade-enter"
        enterActiveClassName="fade-enter-active"
        exitClassName="fade-exit"
        exitActiveClassName="fade-exit-active"
        />
      </Routes>
    </Router>
  );
}

export default App;
