import React, { useEffect, useState } from 'react';
import '../styles/TypingEffect.css';

const TypingEffect = ({ text, subtitle, className }) => {
  const [currentText, setCurrentText] = useState('');
  const [isTypingComplete, setIsTypingComplete] = useState(false);
  const [showSutitle, setShowSubtitle] = useState(false);

  useEffect(() => {
    let isMounted = true;
    let index = 0;

    const typeText = () => {
      if (isMounted) {
        if (index < text.length) {
          setCurrentText(prevText => prevText + text.charAt(index));
          index++;
          setTimeout(typeText, 300); // Adjust typing speed (milliseconds)
        } else {
          setIsTypingComplete(true); // Mark typing as complete
          setTimeout(() => {}

          )
        }
      }
    };

    if (!isTypingComplete) {
      setTimeout(() => {
        typeText();
      }, 1000); // Add a delay of 1000ms (1 second) before the typing effect starts
    }

    return () => {
      isMounted = false;
    };
  }, [text, isTypingComplete]);

  return  <div className="typing-effect">
            <h1 className={`typewriter ${className}`} style={{ wordWrap: 'break-word' }}>
              {currentText}
              <span className="subtitle-90">{subtitle}</span>
            </h1>
          </div>
};

export default TypingEffect;
