import React, { useEffect, useState } from 'react';
import '../styles/TypingEffect.css';


const TypingEffect = ({ text, className }) => {
  setTimeout(() => {

    const [currentText, setCurrentText] = useState('');

    useEffect(() => {
      let isMounted = true;
      let index = 0;

      const typeText = () => {
        if (isMounted) {
          if (index < text.length) {
            setCurrentText(text.slice(0, index + 1));
            index++;
            setTimeout(typeText, 350); // Adjust typing speed (milliseconds)
          } else {
            //Add a delay
            setTimeout(startBlinkEffect, 1000);
          }
        }
      };

      const startBlinkEffect = () => {
        let showCaret = true;

        setTimeout(() => {
          const blinkCaret = () => {
            if (isMounted) {
              // setCurrentText(prevText => (showCaret ? prevText : prevText + '_'));
              showCaret = !showCaret;
              setTimeout(blinkCaret, 500); // Adjust blink speed (milliseconds)
            }
          };
          blinkCaret();
        }, 10000);

      };

      typeText();

      return () => {
        isMounted = false;
      };
    }, [text]);

    return <h1 className={`typewriter ${className}`}>{currentText}</h1>;
  }, 1000);

};

export default TypingEffect;
