import { useLocation } from "react-router-dom";

function ScrollToTop() {
    // Custom hook to manage scroll position on page transitions
    const location = useLocation();
    React.useEffect(() => {
      window.scrollTo(0, 0);
    }, [location.Portfolio]);

    return null;
}

export default ScrollToTop;
