import React, { useEffect, useState } from 'react';
import '../styles/TypingEffect.css';

const TypingEffect = ({ text, className }) => {
  const [currentText, setCurrentText] = useState('');
  const [startTyping, setStartTyping] = useState(false);

  useEffect(() => {
    let isMounted = true;
    let index = 0;

    const typeText = () => {
      if (isMounted && startTyping && text && index < text.length) {
        setCurrentText(prevText => prevText + text.charAt(index));
        index++;
        setTimeout(typeText, 100); // Adjust typing speed (milliseconds)
      } else if (index === text.length) {
        setStartTyping(true); // Stop typing effect once the text is fully displayed
      }
    };

    const startTypingEffect = () => {
      setTimeout(() => {
        setStartTyping(true);
      }, 1000); // Add a delay of 1000ms (1 second) before the typing effect starts
    };

    startTypingEffect();
    typeText(); // Initiate the typing effect after the delay

    return () => {
      isMounted = false;
    };
  }, [text, startTyping]);

  return <h1 className={`typewriter ${className}`}>{currentText}</h1>;
};

TypingEffect.defaultProps = {
  text: '', // Provide a default value for the 'text' prop to prevent issues
};

export default TypingEffect;
